import React, { useState, useEffect } from "react";
import "./KetQuaHocTap.css";

const API_URL = "/api/hocSinh"; // relative path, ch·∫°y chung URL v·ªõi backend

const KetQuaHocTap = () => {
  const [hocSinh, setHocSinh] = useState([]);
  const [tenMoi, setTenMoi] = useState("");
  const [diemA, setDiemA] = useState(""); 
  const [diemB, setDiemB] = useState(""); 
  const [diemC, setDiemC] = useState(""); 
  const [editId, setEditId] = useState(null);
  const [editTen, setEditTen] = useState(""); 
  const [editDiemA, setEditDiemA] = useState(""); 
  const [editDiemB, setEditDiemB] = useState(""); 
  const [editDiemC, setEditDiemC] = useState(""); 
  const [search, setSearch] = useState(""); 

  const fetchData = (keyword = "") => {
    const url = keyword ? `${API_URL}?search=${encodeURIComponent(keyword)}` : API_URL;
    fetch(url)
      .then(res => res.json())
      .then(data => setHocSinh(data))
      .catch(err => console.error(err));
  };

  useEffect(() => { fetchData(); }, []);
  useEffect(() => {
    const t = setTimeout(() => fetchData(search), 400);
    return () => clearTimeout(t);
  }, [search]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!tenMoi || diemA === "" || diemB === "" || diemC === "") return;
    fetch(API_URL, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        ten: tenMoi,
        diemA: parseFloat(diemA),
        diemB: parseFloat(diemB),
        diemC: parseFloat(diemC),
      }),
    })
      .then(res => res.json())
      .then(newHS => {
        setHocSinh(prev => [...prev, newHS]);
        setTenMoi(""); setDiemA(""); setDiemB(""); setDiemC("");
      });
  };

  const handleDelete = (id) => {
    fetch(`${API_URL}/${id}`, { method: "DELETE" })
      .then(() => setHocSinh(prev => prev.filter(h => h.id !== id)));
  };

  const handleEdit = (hs) => {
    setEditId(hs.id);
    setEditTen(hs.ten);
    setEditDiemA(hs.diemA);
    setEditDiemB(hs.diemB);
    setEditDiemC(hs.diemC);
  };

  const handleSave = (id) => {
    fetch(`${API_URL}/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        ten: editTen,
        diemA: parseFloat(editDiemA),
        diemB: parseFloat(editDiemB),
        diemC: parseFloat(editDiemC),
      }),
    })
      .then(res => res.json())
      .then(updated => {
        setHocSinh(prev => prev.map(h => h.id === id ? updated : h));
        setEditId(null);
      });
  };

  return (
    <div className="container">
      <div className="card">
        <h1 className="title">K·∫øt qu·∫£ h·ªçc t·∫≠p</h1>
        <div className="search-row">
          <input
            className="search"
            placeholder="üîç T√¨m ki·∫øm theo t√™n..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
        </div>

        <form className="form" onSubmit={handleSubmit}>
          <input value={tenMoi} onChange={e => setTenMoi(e.target.value)} placeholder="T√™n h·ªçc sinh" />
          <input value={diemA} onChange={e => setDiemA(e.target.value)} placeholder="ƒêi·ªÉm A" type="number" step="0.1" />
          <input value={diemB} onChange={e => setDiemB(e.target.value)} placeholder="ƒêi·ªÉm B" type="number" step="0.1" />
          <input value={diemC} onChange={e => setDiemC(e.target.value)} placeholder="ƒêi·ªÉm C" type="number" step="0.1" />
          <button className="btn primary" type="submit">Th√™m</button>
        </form>

        <div className="table-wrap">
          <table className="table">
            <thead>
              <tr>
                <th>H·ªçc sinh</th>
                <th>A</th>
                <th>B</th>
                <th>C</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {hocSinh.length ? hocSinh.map(hs => (
                <tr key={hs.id}>
                  <td className="name-cell">{editId === hs.id ? <input value={editTen} onChange={e => setEditTen(e.target.value)} /> : hs.ten}</td>
                  <td>{editId === hs.id ? <input type="number" value={editDiemA} onChange={e => setEditDiemA(e.target.value)} /> : hs.diemA}</td>
                  <td>{editId === hs.id ? <input type="number" value={editDiemB} onChange={e => setEditDiemB(e.target.value)} /> : hs.diemB}</td>
                  <td>{editId === hs.id ? <input type="number" value={editDiemC} onChange={e => setEditDiemC(e.target.value)} /> : hs.diemC}</td>
                  <td className="actions">
                    {editId === hs.id ? (
                      <button className="btn save" onClick={() => handleSave(hs.id)}>L∆∞u</button>
                    ) : (
                      <>
                        <button className="btn edit" onClick={() => handleEdit(hs)}>S·ª≠a</button>
                        <button className="btn danger" onClick={() => handleDelete(hs.id)}>X√≥a</button>
                      </>
                    )}
                  </td>
                </tr>
              )) : (
                <tr><td colSpan={5} style={{padding:20}}>Kh√¥ng c√≥ d·ªØ li·ªáu</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default KetQuaHocTap;
